mapper.select:
SELECT w.worklog_id, u.user_name, t.team_name, w.summary, w.written_at FROM worklog w JOIN user u ON w.user_id = u.user_id JOIN team t ON w.team_id = t.team_id WHERE w.team_id = #{teamId}
AND w.written_at BETWEEN #{startDate} AND #{endDate}
AND ( LOWER(u.user_name) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.summary) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.work_content) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.note) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.plan_content) LIKE CONCAT('%', LOWER(#{keyword}), '%') )
AND LOWER(u.user_name) = LOWER(#{keyword})
AND ( LOWER(w.summary) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.work_content) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.note) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.plan_content) LIKE CONCAT('%', LOWER(#{keyword}), '%') )
ORDER BY w.written_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM worklog w JOIN user u ON w.user_id = u.user_id JOIN team t ON w.team_id = t.team_id WHERE w.team_id = #{teamId}
AND w.written_at BETWEEN #{startDate} AND #{endDate}
AND ( LOWER(u.user_name) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.summary) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.work_content) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.note) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.plan_content) LIKE CONCAT('%', LOWER(#{keyword}), '%') )
AND LOWER(u.user_name) = LOWER(#{keyword})
AND ( LOWER(w.summary) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.work_content) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.note) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR LOWER(w.plan_content) LIKE CONCAT('%', LOWER(#{keyword}), '%') )
